class ServiceCard extends StatelessWidget {
  final String name;
  final String description;
  final String svg;

  const ServiceCard({
    super.key,
    required this.name,
    required this.description,
    required this.svg,
  });

  @override
  Widget build(BuildContext context) {
    return Card(
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(10)),
      elevation: 5,
      child: Padding(
        padding: const EdgeInsets.all(8.0), // Reducir el padding para evitar desbordamiento
        child: Column(
          mainAxisAlignment: MainAxisAlignment.spaceEvenly,
          children: [
            Text(
              name,
              style: const TextStyle(
                color: primaryColor,
                fontSize: 16,
                fontWeight: FontWeight.bold,
              ),
              textAlign: TextAlign.center,
            ),
            SvgPicture.string(svg, height: 60, color: secondaryColor), // Reducir la altura del SVG
            Text(
              description,
              style: const TextStyle(color: textColor),
              textAlign: TextAlign.center,
            ),
            SizedBox(
              height: 36, // Altura del botón ajustada
              width: double.infinity,
              child: ElevatedButton(
                onPressed: () {
                  Navigator.push(
                    context,
                    MaterialPageRoute(
                      builder: (context) => PaymentListScreen(serviceName: name),
                    ),
                  );
                },
                style: ElevatedButton.styleFrom(
                  foregroundColor: Colors.white, backgroundColor: accentColor,
                  shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(10),
                  ),
                ),
                child: const Text('Pagar'),
              ),
            ),
          ],
        ),
      ),
    );
  }
}
