                itemCount: services.length,
                itemBuilder: (context, index) {
                  final service = services[index];
                  return ServiceCard(
                    name: service['name']!,
                    description: service['description']!,
                    svg: service['svg']!,
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
